<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Обработка отправки формы.if (!empty($_POST)){	if (articles_new($_POST['title'], $_POST['content']))	{		header('Location: editor.php');		die();	}	$title = $_POST['title'];	$content = $_POST['content'];	$error = true;}else{	$title = '';	$content = '';	$error = false;}// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод в шаблон.$title = "Добавление статьи";// Внутренний шаблон.$content = view_include('view/v_new.php',array('error' => $error));// Внешний шаблон.$page = view_include(	'view/v_main.php',	array('title' => $title, 'content' => $content));// Вывод.echo $page;